package main

import (
	"fmt"
	"os"
	"strconv"
)

const input = ``

func main() {
	codeSizeStr := os.Args[1]
	codeSize, _ := strconv.Atoi(codeSizeStr)

	for i := range input {
		if i >= codeSize {
			miniSet := map[uint8]bool{}
			for n := 0; n < codeSize; n++ {
				if found := miniSet[input[i-n]]; found {
					break
				}
				miniSet[input[i-n]] = true
			}

			if len(miniSet) == codeSize {
				fmt.Printf("first marker detected at: %d\n", i+1)
				return
			}
		}
	}

}
